import './App.css';
import { Button,Form,Input,DatePicker,Row, Col } from 'antd';
import { useState,React } from 'react';
import moment from 'moment'
import axios from 'axios'

function App() {
	const size = 'large'
  const [form] = Form.useForm();
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [inputValue, setInputvalue] = useState('')
  const [stockData, setStockData] = useState([])
  let separator = '-';
  const dayFormat = ['YYYY', 'MM', 'DD'].join(separator);

  const onFinish = (values) => {
    console.log('Success:', values);
  };
  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };
  // const CurrentDate = (date) => {
  //   let result = moment(date).format(dayFormat);
  //   setSelectedDate(result.date)
  //   return result?.date || undefined;
  // };
  const onChangeDate = (date, dateString) => {
    let new_date = moment(dateString).format(dayFormat);
    setSelectedDate(new_date.dateString)
    return new_date?.dateString || undefined;
  };

  const fetchStockData = () => {
    // try {
    //   const response = axios.post('/api/fetchStockData', {
    //     symbol: inputValue,
    //     date: selectedDate,
    //   });
    //   console.log('response.data',response.data)
    //   setStockData(response.data);
    // } catch (error) {
    //   console.error('Error fetching data:', error);
    // }
    axios.post('/api/fetchStockData', {
      symbol: inputValue,
      date: selectedDate
    })
    .then((response) => {
      console.log(response.data);
      setStockData(response.data);
    })
    .catch((error) => {
      console.log(error);
    })
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Stock Analysis</h1>
        {onChangeDate}
        <div style={{marginTop:"0.5rem", border:"1px solid white", minWidth:"350px", padding:'1rem'}}>
          <Form onFinish={onFinish} onFinishFailed={onFinishFailed} autoComplete="off" form={form}>
            <Row>
              <Col span={12} className='gutter-row' style={{padding:'0 12px 0 0'}}>
                <Form.Item rules={[{required: true,message: 'Please Enter Symbol of Stock!'},]}>
                  <Input placeholder="Enter the Symbol of Stock" value={inputValue} onChange={(e) => setInputvalue(e.target.value)}/>
                </Form.Item>
              </Col>
              <Col span={12} className='gutter-row' style={{padding:'0 0 0 12px'}}>
              <Form.Item rules={[{required: true,message: 'Please Enter Symbol of Stock!'},]}>
                <DatePicker onChange={onChangeDate} selected={selectedDate}/>
              </Form.Item>
              </Col>
            </Row>
            <Button type="primary" htmlType='submit' shape="round" size={size} onClick={fetchStockData}>
              Submit
            </Button>
          </Form>
        </div>
      </header>
    </div>
  );
}

export default App;
